import React, { useState, useEffect } from 'react';
import styles from './app.module.css';

// 목표🏆: 앱이 실행되면 유튜브에서 가장 유명한 영상 목록을 보여준다
// 1. 앱이 실행되면 유튜브 API를 이용해서 데이터를 받아온다
// 2. 받아온 데이터를 VideoItem이라는 컴포넌트에서 보여준다
function App() {
  const [vidoes, setVideos] = useState([]);

  useEffect(() => {
    const requestOptions = {
      method: 'GET',
      redirect: 'follow',
    };

    fetch(
      'https://www.googleapis.com/youtube/v3/videos?part=snippet&chart=mostPopular&maxResults=25&key=AIzaSyAtrSZkopq--QXlpEYQ5SrM9Kg5TZlZMl0',
      requestOptions
    )
      .then(response => response.json())
      .then(result => setVideos(result.items))
      .catch(error => console.log('error', error));
    console.log('useEffect');
  }, []);

  return <h1>Hello :)</h1>;
}

export default App;
