import React, { useState, useEffect } from 'react';
import styles from './app.module.css';
import VideoItem from './components/video_item/video_item';

// ëª©í‘œðŸ†: ì•±ì´ ì‹¤í–‰ë˜ë©´ ìœ íŠœë¸Œì—ì„œ ê°€ìž¥ ìœ ëª…í•œ ì˜ìƒ ëª©ë¡ì„ ë³´ì—¬ì¤€ë‹¤
